%% Exercise 3.3(a) % Task: draw the unit circle with the premitive N-th roots of unitity for integer Nn = 7;nth_ROU_list = cal_primitive_nth_ROU(n);draw_unitCircle(n, nth_ROU_list)%% Exercise 3.3(b)% Task: Visualize that sine and (exp(1i*t)-exp(-1i*t))/(2*1i) are equalt = -3*pi:0.01:3*pi;sine = sin(t);diffexp = (exp(1i*t)-exp(-1i*t))/(2*1i);subplot(2,1,2)plot(t,sine-diffexp);title('Sine - exp(1i*t)-exp(-1i*t))/(2*1i)');%%% n denotes the root of unityfunction [n_th_ROU] = cal_primitive_nth_ROU(n)   n_th_ROU = [];for i = 0:n-1        % premitive roots of unity can be calculated by GCD    if gcd(i,n) == 1        n_th_ROU = [n_th_ROU, i];    endendendfunction [] = draw_unitCircle(n, nth_ROU_list)t = 0:0.01:2*pi;z = cos(t)+sin(t)*i;% plot a circlesubplot(2,1,1);plot(real(z), imag(z)); title(['Premitiven ', num2str(n),'-th roots of unity'])hold on;for idx=1 :length(nth_ROU_list)    m = nth_ROU_list(idx);    z = cos(2*pi*m/n)+sin(2*pi*m/n)*i;    plot(real(z),imag(z), '*');endend